import { Button, Modal } from "antd";
import React, { useRef } from "react";

interface PrintPreviewModalProps {
  visible: boolean;
  onClose: () => void;
  children: React.ReactNode;
}

const PrintPreviewModal: React.FC<PrintPreviewModalProps> = ({ visible, onClose, children }) => {
  const printRef = useRef<HTMLDivElement>(null);

  const handlePrint = () => {
    if (printRef.current) {
      const printContents = printRef.current.innerHTML;
      const win = window.open('', '', 'width=900,height=600');
      if (win) {
        win.document.write(`
          <html>
            <head>
              <title>打印预览</title>
              <style>
                @media print {
                  body { margin: 0; }
                }
              </style>
            </head>
            <body>
              <div>${printContents}</div>
            </body>
          </html>
        `);
        win.document.close();
        win.focus();
        setTimeout(() => {
          win.print();
          win.close();
        }, 300);
      }
    }
  };

  return (
    <Modal
      title="打印预览"
      open={visible}
      onCancel={onClose}
      width={900}
      footer={[
        <Button key="print" type="primary" onClick={handlePrint}>
          打印
        </Button>,
        <Button key="close" onClick={onClose}>
          关闭
        </Button>,
      ]}
      style={{ top: 32 }}
      bodyStyle={{ background: '#fff', minHeight: 600 }}
      destroyOnClose
    >
      <div ref={printRef} style={{ background: '#fff', padding: 24 }}>
        {children}
      </div>
    </Modal>
  );
};

export default PrintPreviewModal;
